/* Loading */

function hideLoading() {
  var loadingDiv = document.getElementById('loadingDiv');
  loadingDiv.style.display = 'none';
}
window.addEventListener('load', hideLoading);

/* Language Switch */

function updateContent(language) {
  var elementsToTranslate = document.querySelectorAll("[data-translate]");
  elementsToTranslate.forEach(function (element) {
    var translationKey = element.getAttribute("data-translate");
    element.innerHTML = js_translations[translationKey][language];
  });
}
var nameInput = document.getElementById('name');
var emailInput = document.getElementById('email');
var messageInput = document.getElementById('message');
var currentLanguage = 'es';
function updatePlaceholders(language) {
  nameInput.placeholder = js_translations.contactName[language];
  emailInput.placeholder = js_translations.contactEmail[language];
  messageInput.placeholder = js_translations.contactMessage[language];
}
document.getElementById("language-toggle").addEventListener("change", function () {
  var lang = document.getElementById("language-toggle").checked ? "en" : "es";
  updateContent(lang);
  updatePlaceholders(lang);
  currentLanguage = lang;
  document.getElementById("html").setAttribute("lang", lang);
  var metaDescriptionElement = document.querySelector('meta[class="websiteDescription"][data-translate="metaDescription"]');
  var translatedDescription = js_translations["metaDescription"][lang];
  metaDescriptionElement.setAttribute("content", translatedDescription);
  var video = document.querySelector("video");
  var sourceElement = lang === "es" ? video.querySelector('[data-lang="es"]') : video.querySelector('[data-lang="en"]');
  video.src = sourceElement.getAttribute("src");
  video.load();
});

/* Navbar Functionality */

var sections = document.querySelectorAll("section");
document.addEventListener('scroll', onScroll);
function onScroll() {
  activateMenuAtCurrentSection();
}
function activateMenuAtCurrentSection() {
  var targetLine = scrollY + innerHeight / 2;
  sections.forEach(function (section) {
    var sectionTop = section.offsetTop;
    var sectionHeight = section.offsetHeight;
    var sectionTopReachOrPassedTargetLine = targetLine >= sectionTop;
    var sectionEndAt = sectionTop + sectionHeight;
    var sectionEndPassedTargetLine = sectionEndAt <= targetLine;
    var sectionBoundaries = sectionTopReachOrPassedTargetLine && !sectionEndPassedTargetLine;
    var sectionId = section.getAttribute('id');
    var menuElement = document.querySelector("#mainNavigation a.nav-link[href=\"#".concat(sectionId, "\"], #mainNavigation a.dropdown-item[href=\"#").concat(sectionId, "\"]"));
    menuElement.classList.remove('show');
    if (sectionBoundaries) {
      menuElement.classList.add('show');
    }
  });
}

/* Scroll Progress Bar */

var progressBar = document.getElementById('scroll-progress');
window.addEventListener('scroll', function () {
  var scrollTop = window.scrollY;
  var windowHeight = window.innerHeight;
  var documentHeight = document.body.clientHeight;
  var scrollPercentage = scrollTop / (documentHeight - windowHeight) * 100;
  progressBar.style.width = "".concat(scrollPercentage, "%");
});

/* Scroll Up */

var mybutton = document.getElementById("btn-back-to-top");
window.onscroll = function () {
  scrollFunction();
};
function scrollFunction() {
  if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
    mybutton.style.display = "block";
  } else {
    mybutton.style.display = "none";
  }
}
mybutton.addEventListener("click", backToTop);
function backToTop() {
  document.body.scrollTop = 0;
  document.documentElement.scrollTop = 0;
}

/* Mobile navbar */

document.addEventListener("DOMContentLoaded", function () {
  var menuLinks = document.querySelectorAll('#offcanvasNavbar2 .nav-link, #offcanvasNavbar2 .dropdown-item');
  var offcanvas = new bootstrap.Offcanvas(document.getElementById('offcanvasNavbar2'));
  menuLinks.forEach(function (link) {
    link.addEventListener('click', function () {
      if (!link.classList.contains('dropdown-toggle')) {
        offcanvas.hide();
      }
    });
  });
});
var app_checkbox = document.getElementById("checkbox");
