var index = 0;
var letterIndex = 0;
var isDeleting = false;
function change() {
  var currentWord = changingWords[index][currentLanguage];
  var changingWord = document.querySelector('.changing-word');
  if (!isDeleting) {
    changingWord.textContent = currentWord.slice(0, letterIndex);
    letterIndex++;
    if (letterIndex > currentWord.length) {
      setTimeout(function () {
        isDeleting = true;
      }, 2000);
    }
  } else {
    changingWord.textContent = currentWord.slice(0, letterIndex);
    letterIndex--;
    if (letterIndex === 0) {
      isDeleting = false;
      index = (index + 1) % changingWords.length;
      setTimeout(function () {
        isDeleting = false;
      }, 2000);
    }
  }
}
setInterval(change, 150);

/* Projects Section Tabs */

var tabButtons = document.querySelectorAll('.tab-button');
var categories = document.querySelectorAll('.category');
tabButtons.forEach(function (button) {
  button.addEventListener('click', function () {
    tabButtons.forEach(function (btn) {
      return btn.classList.remove('active');
    });
    button.classList.add('active');
    categories.forEach(function (category) {
      return category.classList.remove('active');
    });
    var categoryToShow = button.getAttribute('data-category');
    document.querySelector(".category.".concat(categoryToShow)).classList.add('active');
  });
});

/* Contact Form */

var formInputs = document.querySelectorAll('.form-input');
formInputs.forEach(function (input, index) {
  input.addEventListener('focus', function () {
    var label = input.previousElementSibling;
    label.style.display = 'block';
    input.removeAttribute('placeholder');
  });
  input.addEventListener('blur', function () {
    var label = input.previousElementSibling;
    if (input.value === '') {
      input.placeholder = label.textContent;
      label.style.display = 'none';
    }
  });
});
